import * as e from "three";

export function setupMockupScene(o, t) {
    let backgroundColor = new e.Color(0xeeeeee),
        wallColor = new e.Color(0xffffff),
        floorColor = new e.Color(0xcccccc);

    o.background = backgroundColor;

    // Floor
    let floorGeo = new e.PlaneGeometry(20, 20),
        floorMat = new e.MeshStandardMaterial({ color: floorColor }),
        floor = new e.Mesh(floorGeo, floorMat);
    floor.rotation.x = -Math.PI / 2;
    floor.position.y = 0;
    o.add(floor);

    // Ceiling
    let ceilingGeo = new e.PlaneGeometry(20, 20),
        ceilingMat = new e.MeshStandardMaterial({ color: wallColor }),
        ceiling = new e.Mesh(ceilingGeo, ceilingMat);
    ceiling.rotation.x = Math.PI / 2;
    ceiling.position.y = 20;
    o.add(ceiling);

    // Back Wall
    let backWallGeo = new e.PlaneGeometry(20, 20),
        backWall = new e.Mesh(backWallGeo, ceilingMat);
    backWall.position.z = -10;
    backWall.position.y = 10;
    o.add(backWall);

    // Front Wall
    let frontWallGeo = new e.PlaneGeometry(20, 20),
        frontWall = new e.Mesh(frontWallGeo, ceilingMat);
    frontWall.rotation.y = Math.PI;
    frontWall.position.z = 10;
    frontWall.position.y = 10;
    o.add(frontWall);

    // Left Wall
    let leftWallGeo = new e.PlaneGeometry(20, 20),
        leftWall = new e.Mesh(leftWallGeo, ceilingMat);
    leftWall.rotation.y = Math.PI / 2;
    leftWall.position.x = -10;
    leftWall.position.y = 10;
    o.add(leftWall);

    // Right Wall
    let rightWallGeo = new e.PlaneGeometry(20, 20),
        rightWall = new e.Mesh(rightWallGeo, ceilingMat);
    rightWall.rotation.y = -Math.PI / 2;
    rightWall.position.x = 10;
    rightWall.position.y = 10;
    o.add(rightWall);

    let tableGeo = new e.BoxGeometry(10, 1, 5),
    tableMat = new e.MeshStandardMaterial({ color: 0x8B4513 }),
    table = new e.Mesh(tableGeo, tableMat);
table.position.set(0, 2, -5);
o.add(table);

// Add small legs to the table (inline approach)
let legGeo = new e.BoxGeometry(1, 2, 1);
let legMat = new e.MeshStandardMaterial({ color: 0x8B4513 });

// Positions for the four legs (relative to the table center)
[ [ 4.5, -1,  2 ], 
  [-4.5, -1,  2 ],
  [ 4.5, -1, -2 ],
  [-4.5, -1, -2 ] 
].forEach(([x, y, z]) => {
    let leg = new e.Mesh(legGeo, legMat);
    leg.position.set(x, y, z);
    table.add(leg);
});

    // Chairs
   
    // Ambient Light
    let ambientLight = new e.AmbientLight(0x404040, 2);
    o.add(ambientLight);

    // Directional Light
    let dirLight = new e.DirectionalLight(0xffffff, 1);
    dirLight.position.set(10, 20, 10);
    o.add(dirLight);
    
}
